function changeClassName(e,n){for(var t=0;t<e.length;t++)e[t].className=n}document.addEventListener("DOMContentLoaded",function(){for(var e=null,n=document.getElementsByTagName("td"),t=0;t<n.length;t++)n[t].addEventListener("click",function(){null==e?(e=this,changeClassName(n,""),this.className="selected"):e==this?(this.className="",e=null,changeClassName(n,"unclick")):(changeClassName(n,""),(e=this).className="selected")});document.getElementById("goBtn").addEventListener("click",function(){e?window.location.href="wordReader.html?wordList="+e.id:alert("Please Choose One!")})});